import React from "react";

function Todo({ todo, index, completeTodo, removeTodo }) {
    return (
      <div
        className="todo"
        style={
          { 
          textDecoration: todo.isCompleted ? "line-through" : "" ,
          textDecorationColor: todo.isCompleted ? "red" : "  color: #0074D9;"
          }

        }
      >
        <div
          className="todo-items">
          <button onClick={() => completeTodo(index)}><span role="img" aria-label="checkmark">&#x2714;</span></button>
          {todo.text}
          <button onClick={() => removeTodo(index)}><span role="img" aria-label="X">&#x274C;</span></button>
        </div>
      </div>
    );
  }

  export default Todo;
